package com.pip.annotation;

import java.lang.annotation.*;

/**
 * 注解，Action是否需要登录才能访问，配合拦截器使用com.pip.interceptor.LoginInterceptor
 * Created by djw on 2015/9/2.
 */
@Target(ElementType.METHOD)
@Retention(RetentionPolicy.RUNTIME)
@Inherited
@Documented
public @interface ActionNeedLogin {
    public boolean need() default true;
}